class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def helper(stair, jump, downUsed):
            if (stair - k) > 1:
                return 0

            count = 0
            if stair == k:
                count += 1
            if not downUsed:
                count += helper(stair - 1, jump, True)

            count += helper(stair + 2 ** jump, jump + 1, False)
            return count

        return helper(1, 0, False)
