#include <unordered_map>
#include <utility>

class Solution {

public:

    auto waysToReachStair(int k) {

        if (k == 0) {
            return 2ll;
        }

        auto dp = std::unordered_map<long long, long long>();
        auto tot_ways = 0ll;

        dp[0] = 1;
        dp[1] = 1;

        tot_ways += dp[k];

        for (auto i = 0; 1ll << i <= k * 2; ++i) {
            auto new_dp = std::unordered_map<long long, long long>();
            for (const auto& [step, ways] : dp) {
                new_dp[step + (1 << i)] += ways;
                new_dp[step + (1 << i) - 1] += ways;
            }
            dp = std::move(new_dp);
            tot_ways += dp[k];
        }

        return tot_ways;

    }

};
