#define lli long long
vector<lli>power(31);
void find(){
    lli p=1;
    for(lli i=0;i<=30;i++){
        power[i]=p;
        p=p*2;
    }
}
class Solution {
public:
    lli fuc(lli i,lli p,lli q,map<pair<lli,pair<lli,lli>>,lli>&m,int &k){
        if(q>30){
            return m[{i,{p,q}}]=0;
        }
        if(m.find({i,{p,q}})!=m.end()){
            return  m[{i,{p,q}}];
        }
        lli ans=0;
        if(i==k){
            ans=1;
        }
        if(q==0){
            ans+=fuc(i-1,p,1,m,k);
        }
        if(p<=30&&i+power[p]<=k+1){
            ans+=fuc(i+power[p],p+1,0,m,k);
        }
        return m[{i,{p,q}}]=ans;
    }
    int waysToReachStair(int k) {
        find();
        map<pair<lli,pair<lli,lli>>,lli>dp;
        return (int)(fuc(1,0,0,dp,k));
    }
};
