class Solution:
    def waysToReachStair(self, k: int) -> int:
        gap = k-1
        ans = 0
        cnt = 0
        cursum = 0
        while True:
            if cursum-cnt-1 <= gap <= cursum:
                ans += math.comb(cnt+1, gap-(cursum-cnt-1))
            if cursum-cnt-1 > gap and cnt > 2:
                break
            cursum += 2**cnt
            cnt += 1
        return ans
