class Solution {
public:
    int C(int n, int r) {
        if(r > n/2) r = n-r;
        long long ans = 1;
        for(int i = 1; i <= r; i++) {
            ans *= n-r+i;
            ans /= i;
        }
        return (int)ans;
    }

    int waysToReachStair(int k) {
        if(k == 0) return 2;
        if(k == 1) return 4;
        int x = 1;
        int step = 1;
        int jump = 1;
        int ans = 0;
        while(step < k) {
            step += jump;
            x++;
            jump*=2;
        }
        if(step == k) {
            ans++;
            step+=jump;
            x++;
        }
        int rem = step - k;
        if(rem <= x) {
            ans += C(x, rem);
        }
        return ans;

    }
};
