class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 0
        @cache
        def rec(i, jump, backJump):
            nonlocal ans
            if i - 1 > k:
                return 0
            ans = 0
            if i == k:
                ans += 1
            if i != 0 and not backJump:
                ans += rec(i - 1, jump, True)
            ans += rec(i + 2 ** jump, jump + 1, False)
            return ans
        return rec(1, 0, False)
