class Solution:
    def waysToReachStair(self, k: int) -> int:
        def check(num, i):
            if num < 0:
                return False
            bits = []
            while num:
                bits.append(num & 1)
                num //= 2
            return sum(bits) == len(bits) and len(bits) + 1 >= i
        ans = 0
        for i in range(35):
            tgt = k - 1 + i
            if check(tgt, i):
                ans += comb(tgt.bit_count() + 1, i)
        return ans
