class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def go(pos, lastDown, jump):
            if pos - 2 > k:
                return 0
            total = pos == k

            if not lastDown and pos != 0:
                total += go(pos - 1, True, jump)

            total += go(pos + 2 ** jump, False, jump + 1)
            #print(total)
            return total
        #print('---')
        return go(1, False, 0)
