class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def helper(index, used, jump):
            if(index-2 > k): return 0

            if(index == k):
                if(used or index == 0): return 1 +  helper(index + 2**jump, False, jump+1)
                return 1 + helper(index-1, True, jump) + helper(index + 2**jump, False, jump+1)

            if(used or index == 0): return helper(index + 2**jump, False, jump+1)
            return helper(index-1, True, jump) + helper(index + 2**jump, False, jump+1)

        ans = helper(1, False, 0)
        print(ans)

        return ans
