class Solution:
    def waysToReachStair(self, k: int) -> int:

        @functools.lru_cache(None)
        def dfs(pos, jump, pre_op1):
            ans = 0
            if pow(2, jump) > 10 ** 11 and pos-1 != k:
                return 0
            if pos == k:
                ans += 1

            res1 = res2 = 0
            if pre_op1:
                res1 += dfs(pos+pow(2, jump), jump+1, False)
            else:
                res2 += dfs(pos-1, jump, True)
                res2 += dfs(pos+pow(2, jump), jump+1, False)
            return ans + res1 + res2

        return dfs(1, 0, False)
