class Solution {
    public int waysToReachStair(int k) {
        k--;
        int ans=0;
        long l=0;
        int c=1;
        //if(k==0) ans++;
        while(l<=Math.pow(2,34)){
            if(k>=l-c && k<=l){
                int b=(int)(l-(long)k);
                ans+=ncr(c,Math.min(b,c-b));
                }
            c++;
            l=l*2+1;
            }
       return ans;
    }
    public int ncr(int n,int r){
        double sum = 1;
        for (int i = 1; i <= r; i++) {
            sum = sum * (n - r + i) / i;
        }
        return (int)sum;
        }
}
