def ncr(n, r):
    if r > n:
        return 0
    return factorial(n) // (factorial(r) * factorial(n - r))

class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 0
        curr = 1

        if k <= 1:
            ans += 1

        for i in range(31):
            curr += (1 << i)
            j = i + 1
            x = curr - k
            if x >= 0 and x - j <= 1:
                y = ncr(j + 1, x)
                # print(y)
                ans += y

        return ans
