class Solution {
public:
    int powerUp(int n) {
        int i;
        for(i = 0; n > (1 << i); ++i);
        return (1 << i);
    }
    int log2(int p) {
        if (p == 1) {
            return 0;
        }
        return 1 + log2(p / 2);
    }
    int nCr(int n, int r) {
        __int128 val = 1;
        for(int i = 0; i < r; ++i) {
            val *= (n - i);
        }
        for(int i = 1; i <= r; ++i) {
            val /= i;
        }
        return val;
    }
    int waysToReachStair(int k) {
        // k = 0 through 8 are special...
        switch (k) {
            case 0: return 2;
            case 1: return 4;
            case 2: return 4;
            case 4: return 2;
        }
        int n = powerUp(k);
        int m = 1 + log2(n);
        if (n - m > k) {
            return 0;
        }
        return nCr(m, n - k);
    }
};
