class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dp(i, jump, used):
            ans = 0
            if i == k:
                ans = 1
            if i > k:
                if not (i == k + 1 and not used):
                    return 0
                ans = 0
            ans += dp(i + (1 << jump), jump + 1, False)
            if not used and i != 0:
                ans += dp(i - 1, jump, True)
            return ans

        return dp(1, 0, False)
