class Solution {
public:
    int helper(int i,int j,int p,map<int,map<int,map<int,int>>> &dp,int k){
         if(i > k+1) return 0;
         if(dp[i][j].find(p)!=dp[i][j].end()) return dp[i][j][p];
         int count = (i==k)?1:0;
         int t1 = helper(i+(1<<j),j+1,0,dp,k);
         int t2 = 0;
        if(p==0 && i!=0) t2 = helper(i-1,j,1,dp,k);
        return dp[i][j][p] = count+t1+t2;
    }
    int waysToReachStair(int k) {
       map<int,map<int,map<int,int>>> dp;
        return helper(1,0,0,dp,k);
    }
};
