class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def recourse(n, power, detract):
            ans = 0

            if n > k + 1:
                return 0
            if n == k:
                ans += 1
            if not detract and n != 0:
                ans += recourse(n - 1, power, True)
            ans += recourse(n + 2**power, power + 1, False)
            return ans
        return recourse(1, 0, False)
