class Solution {
public:
    map <int, map<int,map<int,int>>> dp;
    int k;
    int f(int ind, int jump, int p){
        if(ind > k+1) return 0;
        if(dp[ind][jump].find(p) != dp[ind][jump].end()) return dp[ind][jump][p];

        int cnt = 0;
        if(ind == k) cnt++;

        int f1 =  f(ind + (1<<jump), jump + 1, 0);
        int f2 = 0;
        if(p == 0 && ind != 0) f2 = f(ind -1, jump, 1);

        return dp[ind][jump][p] = cnt + f1 + f2;
    }

    int waysToReachStair(int K) {
        k = K;
        int ans = 0;
        ans = f(1,0,0);
        return ans;
    }
};
