import 'dart:collection';

class Solution {
  final Map<int, Map<int, Map<bool, int>>> dp = {};

  int solve(int k, int i, int jump, bool can) {
    if (i > k + 5) {
      return 0;
    }

    if (jump > 31) {
      return 0;
    }

    if (dp.containsKey(i) && dp[i]!.containsKey(jump) && dp[i]![jump]!.containsKey(can)) {
      return dp[i]![jump]![can]!;
    }

    int ans = (i == k) ? 1 : 0;
    if (can) {
      ans += solve(k, i - 1, jump, false);
    }
    if (i + (1 << jump) <= k + 1) {
      ans += solve(k, i + (1 << jump), jump + 1, true);
    }

    dp.putIfAbsent(i, () => {});
    dp[i]!.putIfAbsent(jump, () => {});
    dp[i]![jump]!.putIfAbsent(can, () => 0);
    dp[i]![jump]![can] = ans;

    return ans;
  }

  int waysToReachStair(int k) {
    return solve(k, 1, 0, true);
  }
}
