class Solution {
public:
    int waysToReachStair(int k) {

        unordered_map<string, long long> memset;

        return static_cast<int>(dfs(1, 0, false, k, memset));
    }

    long long dfs(int Step, int jump, bool Dec, int target, unordered_map<string, long long>& memset) {

        if (Step > target + 1) {
            return 0;
        }

        string key = to_string(Step) + "," + to_string(jump) + "," + to_string(Dec);

        if (memset.find(key) != memset.end()) {
            return memset[key];
        }

        long long count = 0;

        if (Step == target) {
            count++;
        }

        if (Step > 0 && !Dec) {
            count += dfs(Step - 1, jump, true, target, memset);
        }

        count += dfs(Step + (1 << jump), jump + 1, false, target, memset);

        memset[key] = count;

        return count;
    }
};
