#define ll long long
const ll mod = 1000000007;

ll bin_exp(ll a, ll b=1000000005, ll mod=1000000007){
    ll ans = 1;
    while (b){
        if(b % 2) ans = (ans * a) % mod;
        a = (a * a) % mod; b /= 2;
    }
    return ans;
}

class Solution {
public:
    int waysToReachStair(int k) {
        ll ans = 0, L = 0;
        if(k <= 1) ans = 1;
        // debug(ans);
        for(int i = 1;; ++i){
            // done i jump operation
            L += (1ll << (i - 1));
            if(1 + L < k) continue;
            else if(L + 1 > (k + i + 1)) break;
            else{
                ll rr = L + 1 - k;
                ll nn = i + 1;
                ll f_n = 1, f_r = 1, f_nr = 1;
                for(int m = 1; m <= nn; ++m) f_n *= m, f_n %= mod; 
                for(int m = 1; m <= rr; ++m) f_r *= m, f_r %= mod; 
                for(int m = 1; m <= nn - rr; ++m) f_nr *= m, f_nr %= mod; 

                ll temp = ((f_n * bin_exp(f_r) % mod) * bin_exp(f_nr)) % mod;
                // debug(L, i, nn, rr, temp);
                // debug(f_n, f_r, f_nr);
                ans += temp;
            }
        }
        return ans;
    }
};
