class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i, jump, can_go_down):
            if i == k:
                ans = 1

                if jump == 0:
                    ans += dp(i + 1, jump + 1, True)
                if can_go_down:
                    ans += dp(i - 1, jump, False)

                return ans

            if i == k + 1:
                if not can_go_down:
                    return 0

                return dp(i - 1, jump, False)
                # return 2 if jump == 0 else 1

            if i > k + 1:
                return 0

            ans = dp(i + 2**jump, jump + 1, True)

            if can_go_down and i >= 1:
                down = dp(i - 1, jump, False)
                ans += down

            return ans


        return dp(1, 0, True)
