class Solution:
    def waysToReachStair(self, k: int) -> int:
        mem = {}
        def dfs(stair, jump, lastWas1):
            if stair > k + 1:
                return 0
            if (stair, jump, lastWas1) in mem:
                return mem[(stair, jump, lastWas1)]

            ways = 0
            if stair == k:
                ways += 1
            if not lastWas1 and stair > 0:
                ways += dfs(stair - 1, jump, True)
            ways += dfs(stair + 2**jump, jump + 1, False)
            mem[(stair, jump, lastWas1)] = ways
            return ways

        return dfs(1, 0, False)
