/**
 * @param {number} k
 * @return {number}
 */
var waysToReachStair = function(k) {
    const memo = new Map();
    var rec = (curr,jump,last) => {
        if((last && curr>=k+1) || curr>k+1) {
            return 0;
        }
        let key = `${curr}_${jump}_${last}`;
        if(memo.has(key)) return memo.get(key);
        let ans = 0;
        if(curr === k) {
            ans++;
        }

        let op1 = (!last) ? rec(curr-1,jump,1) : 0;
        let op2 = rec(curr+2**jump,jump+1,0);

        memo.set(key,ans + op1 + op2);
        return memo.get(key);
    }

    return rec(1,0,0);
};
