class Solution
{

private:
    int k;
    int func(ll i, ll jump, int last, map<vl, int> &m)
    {
        if (i >= k + 2 || (i == k + 1 && last))
            return 0;

        vl tv = {i, jump, last};
        if (m.find(tv) != m.end())
            return m[tv];

        int ans = i == k;
        if (last)
            ans += func(i + (1ll << jump), jump + 1, 0, m);
        else
            ans += func(i - 1, jump, 1, m) + func(i + (1ll << jump), jump + 1, 0, m);

        return m[tv] = ans;
    }

public:
    int waysToReachStair(int top)
    {
        k = top;
        map<vl, int> m;
        return func(1, 0, 0, m);
    }
};
