class Solution {
    map<tuple<int, bool, int>, int> dp;

public:
    int waysToReachStair(int k) {
        return call(1, false, 0, k);
    }
    int call(int step, bool used, int jump, int k) {
        if (step > k + 1) {
            return 0;
        }
        if (dp.count({step, used, jump})) {
            return dp[{step, used, jump}];
        }
        int res = step == k;
        if (!used) res += call(step - 1, 1, jump, k);
        res += call(step + (1 << jump), 0, jump + 1, k);
        return dp[{step, used, jump}] = res;
    }
};
