class Solution 
{
private:
    map<vector<int>, int> mp;
public:
    int waysToReachStair(int k, int idx = 1, int jump = 0, bool flag = true)
    {
        if(mp.count({idx, jump, flag}))
            return mp[{idx, jump, flag}];
        int ans = 0;
        if(idx == k)
            ans++;
        if(flag && idx != 0)
            ans += waysToReachStair(k, idx-1, jump, false);
        if((idx+(1<<jump)-1 <= k))
            ans += waysToReachStair(k, idx+(1<<jump), jump+1, true);
        return mp[{idx, jump, flag}] = ans;
    }
};
