class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i, jump, can_drop):
            res = (i == k)
            if i > 0 and can_drop:
                res += dp(i-1, jump, False)
            if i <= k+1:
                res += dp(i + (1<<jump), jump+1, True)
            return res

        return dp(1, 0, True)
