import math
class Solution:
    def waysToReachStair(self, k: int) -> int:
        k -= 1
        a = 0
        ans = 0
        if k < 0:
            ans += 1
        if k == 0:
            ans += 1
        for j in range(200):
            a += (1<<j)
            if a < k:
                continue
            d = a-k
            if d > j+2:
                break
            ans += math.comb(j+2,d)

        return ans
