class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(jump, idx, prev):



            if idx > k +1:
                return 0
            ans = 0 
            if idx == k:
                ans += 1

            if not prev:
                ans += dp(jump, idx-1, True)

            ans += dp(jump+1, idx + (2**jump), False)
            return ans

        return dp(0, 1, False)
