class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        upper = ceil(log2(k)) + 4

        res = 0
        for i in range(upper):
            max_forward = 2 ** i - 1
            for j in range(i + 2):
                if max_forward + 1 - j == k:
                    res += comb(i + 1, j)

        return res
