@cache
def frac(n):
    if n == 0:
        return 1
    return n * frac(n - 1)





class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 0

        def check_jump(t):
            tot = (1 << t) 
            n_back_max =  t + 1 #0 to t 
            if tot < k :
                return 0
            if tot - n_back_max > k:

                return 0
            else:
                n_back = tot - k
                #print(t + 1, n_back)
                return comb(t + 1, n_back) 

        for i in range(51):
            #print(i, check_jump(i))
            ans += check_jump(i)
        return ans
