class Solution(object):
    def __init__(self):
        self.dp = {}

    def solve(self, target, current, steps, can_jump):
        if current > target + 5:
            return 0

        if steps > 31:
            return 0

        if (current, steps, can_jump) in self.dp:
            return self.dp[(current, steps, can_jump)]

        ans = (current == target)
        if can_jump:
            ans += self.solve(target, current - 1, steps, False)
        if current + (1 << steps) <= target + 1:
            ans += self.solve(target, current + (1 << steps), steps + 1, True)

        self.dp[(current, steps, can_jump)] = ans
        return ans

    def waysToReachStair(self, target):
        """
        :type target: int
        :rtype: int
        """
        return self.solve(target, 1, 0, True)
