class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dfs(x, jump):
            if x > k + 1:
                return 0

            res = (x == k) + dfs(x + jump, 2 * jump)
            if x > 0:
                res += ((x - 1) == k) + dfs(x - 1 + jump, 2 * jump)

            return res

        return dfs(1, 1)
