typedef long long int ll; 
typedef unsigned long long ull;
typedef long double ld;       
#define pb push_back
#define pob pop_back
#define ff first
#define ss second
#define all(v) (v).begin(), (v).end()
#define sare(v) (v).begin(), (v).end()
#define MOD 1000000007

class Solution {
public:
    map <pair<ll,pair<ll,ll> >,ll > mp; 

    ll k;
    ll rec(ll x,ll jump,ll t)
    {
        if(x>k+1)
        {
            return 0;
        }
        if(mp.count({x,{jump,t}})) return mp[{x,{jump,t}}];
        ll ans=0;
        if(x==k) ans++;
        if(jump<32)
        ans+=rec(x+(1<<jump),jump+1,0);
        if(x!=0&&t==0)
        {
            ans+=rec(x-1,jump,1);
        }
        return mp[{x,{jump,t}}]=ans;
    }

    int waysToReachStair(int x) {
        mp.clear();
        k=x;
        return rec(1,0,0);
    }
};
