class Solution {
public:
    map<pair<int,pair<int,int>>,int>dp;
    int fun(int x,int ok,int i,int k){
        if(x == k)return 1;
        if(x > k+1)return 0;

        if(dp.find({x,{ok,i}} )!= dp.end())return dp[{x,{ok,i}}];


        int xx=0,yy=0,zz=0;
        if(ok){
             xx = fun(x+(1<<i),1-ok,i+1,k);
        }else {
             yy= fun(x+(1<<i),ok,i+1,k);
             if(x)
             zz=fun(x-1,1-ok,i,k);
        }
        return dp[{x,{ok,i}}] = xx+yy+zz;

    }
    int waysToReachStair(int k) {
        if(k==0)return 2;
        if(k==1)return 4;
        if(k==2)return 4;
        dp.clear();
        return fun(1,0,0,k);
    }
};
