#define ll long long int

class Solution {
public:

    ll fun(ll st, ll jump, ll p, int &k, map<tuple<ll,ll,ll>,ll>&m){

        ll val=st+(1<<jump);
        if((val-1)>k && (st-1)>k){
            return 0;
        }

        if(m.find({st,jump,p}) !=m.end()){
            return m[{st,jump,p}];
        }

        ll ans=(fun(val,jump+1,0,k,m)) + (val==k);
        if(!p){
            if(st>0){
                ans+=(fun(st-1, jump, 1, k, m)) + ((st-1)==k);
            }
        }
        return m[{st,jump,p}]=ans;
    }

    int waysToReachStair(int k) {
        map<tuple<ll,ll,ll>,ll>m;
        return fun(1ll, 0ll, 0ll, k, m) + (k==1);
    }
};
