class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def count(curr, jump):
            if curr >= k + 2:
                return 0
            elif curr == k + 1:
                return 1 + count(curr - 1 + jump, jump * 2)
            elif curr == k:
                return 1 + count(curr - 1 + jump, jump * 2) + count(curr + jump, jump * 2)
            else:
                return count(curr - 1 + jump, jump * 2) + count(curr + jump, jump * 2)
        return count(1, 1)
