class Solution:
    def waysToReachStair(self, k: int) -> int:

        res = 0

        dp = {1: 1}
        for i in range(30):
            ndp = {}

            a, b = 2 ** i, 2 ** i - 1
            for prev, cnt in dp.items():
                if prev - 1 > k:
                    continue
                elif prev == k or prev - 1 == k:
                    res += cnt

                na, nb = prev + a, prev + b
                if na not in ndp:
                    ndp[na] = 0
                if nb not in ndp:
                    ndp[nb] = 0
                ndp[na] += cnt
                ndp[nb] += cnt

            dp = ndp
            if not dp:
                break

        return res
