class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dfs(n, jump, p):
            ways = 0
            if n>k+1:
                return 0
            if n==k:
                ways += 1
            if p==0 and n>0:
                ways += dfs(n-1, jump, 1)
            ways += dfs(n+(1<<jump), jump+1, 0)
            return ways

        return dfs(1, 0, 0)
