func waysToReachStair(k int) int {
    return dfs(map[[3]int]int{}, k, 1, 0, true)
}

func dfs(mem map[[3]int]int, k, p, j int, down bool) (ans int) {
    key := [3]int{p, j, 0}
    if down {
        key[2] = 1
    }
    if v, ok := mem[key]; ok {
        return v
    }
    if k == p {
        ans += 1
    }
    if down && p != 0 {
        ans += dfs(mem, k, p-1, j, false)
    }
    if p + (1<<j)-1 <= k {
        ans += dfs(mem, k, p+1<<j, j+1, true)        
    }
    mem[key] = ans
    return ans
}
