class Solution {
public:
        using ll = long long;
        ll nCr(ll n,ll r)
        {
                if(r>n)
                        return 0;
                if(r==0 || r==n)
                        return 1;

                ll p=1;
                for(ll i=1;i<=r;i++)
                {
                        p*=n-(r-i);
                        p/=i;
                }
                return p;
        }

    int waysToReachStair(int k) {          
            int ans=0;
            for(int bit=0;bit<32;bit++)
            {
                    int temp = (1LL<<bit)-k;
                    if(temp>=0)
                    {
                            ans+=(nCr(bit+1,temp));
                    }
            }
            return ans;
    }
};
