class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def rec(pos, back, j):
            if pos > k + 1:
                return 0
            ans = int(pos == k)
            if back:
                ans += rec(pos - 1, False, j)
            ans += rec(pos + 2**j, True, j + 1)
            return ans

        return rec(1, True, 0)
