class Solution:
    def waysToReachStair(self, k: int) -> int:
        d = {}
        def recur(idx,j,prev):
            if idx>k+1:
                return 0
            if (idx,j,prev) in d:
                return d[(idx,j,prev)]
            ans = 0
            if idx==k:
                ans+=1
            a = recur(idx+(2**j),j+1,False)
            b=0
            if not prev:
                b = recur(idx-1,j,True)
            d[(idx,j,prev)] = ans+a+b
            return ans+a+b
        return recur(1,0,False)
