class Solution {
public:
    #define ll long long
    map<string,ll>m;
    ll func(ll cur,ll j,ll last,ll k){
        if((cur-1)>k)return 0;

        string str="";
        str+=to_string(cur);
        str+="#";
        str+=to_string(j);
        str+="#";
        str+=to_string(last);

        if(m.count(str)>0)return m[str];

        ll curans=0;
        if(last==0){
            curans+=func(cur+(1LL<<j),j+1,1,k);
        }
        else{
            curans+=func(cur+(1LL<<j),j+1,1,k);
            curans+=func(cur-1,j,0,k);
        }

        if(cur==k)curans+=1;
        return m[str]=curans;
    }
    int waysToReachStair(int k) {
        m.clear();
        return func(1,0,1,k);
    }
};
