class Solution {
public:
    #define ll long long
    ll solve(ll i,ll jump,bool flag,ll k,map<string,int>&dp){
        if(i>k && flag==1) return 0 ;
        if(i==k){
            ll ans1 = 0;
            if(flag==0) ans1 = solve(i-1,jump,1,k,dp) ;
            ll ans2=0 ;
            if(jump<30) ans2 = solve(i+pow(2,jump),jump+1,0,k,dp) ;
            return ans1+ans2+1 ;
        }
        string s = to_string(i)+" "+to_string(jump)+" "+to_string(flag) ;
        if(dp.find(s)!=dp.end()) return dp[s] ; 
        ll ans1=0 ;
        if(flag==0) ans1 = solve(i-1,jump,1,k,dp) ;
        ll ans2=0 ;
        if(jump<30) ans2 = solve(i+pow(2,jump),jump+1,0,k,dp) ;
        return dp[s] = ans1+ans2 ;
    }


    int waysToReachStair(int k) {
        map<string,int>dp ;
        ll ans = solve(1,0,0,k,dp) ;
        return ans ;
    }
};
