from math import comb
from typing import List


class Solution:
    def waysToReachStair(self, k: int) -> int:
        jump = 0
        ans = 0
        for back in range(32):
            delta = k - 1 + back
            while (1 << jump) - 1 < delta:
                jump += 1

            if (1 << jump) - 1 == delta and back <= jump + 1:
                ans += comb(jump + 1, back)

        return ans
