class Solution:
    def waysToReachStair(self, k: int) -> int:
        max_jump = floor(log2(k + 1)) + 1
        print(max_jump)
        @cache
        def dfs(i: int, jump: int, flag: bool) -> int:
            if jump > max_jump:
                return 0
            ways = 0
            if i == k:
                ways= 1
            next_pos = i + (1 << jump)
            if next_pos <= k+1:
                ways += dfs(next_pos, jump + 1, False)

            if not flag and i > 0:
                ways += dfs(i - 1, jump, True)

            return ways

        return dfs(1,0,False)
