typedef long long ll;

ll comb(ll x, ll p) {
    ll z = 1;
    if (p > x / 2) {
        p = x - p;
    }
    vector<ll> as, bs;
    for (ll u = x; u >= x - p + 1; u --) {
        as.push_back(u);
    }
    for (ll u = p; u >= 1; u --) {
        bs.push_back(u);
    }
    ll d;
    for (auto &x: as) {
        for (auto &y: bs) {
            if ((d = gcd(x, y)) > 1) {
                x /= d;
                y /= d;
            }
        }
    }
    for (auto x: as) {
        z *= x;
    }
    return z;
}

class Solution {
public:
    int waysToReachStair(int k) {
        ll ans = 0;
        for (ll i = -1; i <= 30; i ++) {
            ll tt = (1ll << (i + 1));
            // cout << (i + 1) << ' ' << (tt - k) << '\n';
            if (tt >= k && tt - k <= (i + 2)) {
                ll U = tt - k;
                ans += comb(i + 2, U);
            }
        }
        return ans;
    }
};
