class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def nextJump(j: bool, jump: int, pos: int):
            res = 0
            if pos == k:
                res += 1
            if not j:
                res += nextJump(True, jump, pos - 1)
            nextPos = pos + (1 << jump)
            if nextPos - k > 1:
                return res
            res += nextJump(False, jump + 1, nextPos)
            return res
        return nextJump(False, 0, 1)
