class Solution {
public:
    int NCR(int n, int r)
    {
        if (r == 0) return 1;
        if (r > n / 2) return NCR(n, n - r); 
        long res = 1; 
        for (int k = 1; k <= r; ++k)
        {
            res *= n - k + 1;
            res /= k;
        }
        return res;
    }
    int waysToReachStair(int k) {
        long long start = 1;
        int jump = 0;
        int ways = 0;
        if (k < 2)
            ways++;
        while (jump < 32) {
            start += pow(2, jump);
            if (k <= start) {
                int diff = start - k;
                if (diff <= jump + 2) {
                    ways += NCR(jump+2,diff);
                } else
                    break;
            }
            jump++;
        }
        return ways;
    }
};
